import React, { useState } from 'react';
import { Button, Form, Grid, Header, Message, Segment, Icon } from 'semantic-ui-react';

const LoginForm = () => {
  const [userFormData, setUserFormData] = useState({ username: '', password: '' });
  const [validated] = useState(false);
  const [showAlert, setShowAlert] = useState(false);



  handleSubmit = (event) => {
    event.preventDefault();
  }


  handleChange = (event) => {
    const { name, value } = event.target;
    setUserFormData({ ...userFormData, [name]: value })
  };

  render() 
    return (






      <Grid textAlign='center' style={{ height: '100vh' }} verticalAlign='middle'>
        <Grid.Column style={{ maxWidth: 500 }}>

          <Header as='h2' color='blue' textAlign='Center'>
            <Icon name='sign in' size='big'></Icon>Login to your account</Header>
          <Segment >
            <Form>
              <Form.Input fluid icon='user' iconPosition='left' name='username' />
              <Form.Input
                fluid
                icon='lock'
                iconPosition='left'
                placeholder='password'
                type='password'
              />
              <Button onSubmit={this.handleSubmit} fluid size='Large' color='blue'>Submit</Button>
            </Form>
          </Segment>
          <Message>
            {/Link below need to link to our sign-up page/}
            Need to {/*<a href='#'> Sign-up?</a>*/}
          </Message>
        </Grid.Column>
      </Grid>
    );
  }



export default LoginForm;
